import {useState} from "react";



function App() {

  const DUMMY_DATA = [
    { category: "Fruits", price: "$1", stocked: true, name: "Apple" },
    { category: "Fruits", price: "$1", stocked: true, name: "Dragonfruit" },
    {
      category: "Fruits",
      price: "$2",
      stocked: false,
      name: "Passionfruit",
    },
    { category: "Vegetables", price: "$2", stocked: true, name: "Spinach" },
    {
      category: "Vegetables",
      price: "$4",
      stocked: false,
      name: "Pumpkin",
    },
    { category: "Vegetables", price: "$1", stocked: true, name: "Peas" },
  ];

  const [searchterm, setSearch] = useState("");
  const [inStockOnly, setInStockOnly] = useState(false);
  return (
    <div className="App">
      <SearchForm searchTerm={searchterm} inStockOnly={inStockOnly} />
      <p></p>
      <ProductsTable products={DUMMY_DATA} />
    </div>
  );
}


function SearchForm({searchTerm, inStockOnly}) {
  return (
    <form action="GET">
      <div className="form-group">
        <input type="search" placeholder="Search..." value={searchTerm}></input>
      </div>
      <div className="form-group">
        <label htmlFor="filter">In Stock items Only</label>
        <input type="checkbox" id="filter" inStockOnly />
      </div>
    </form>
  );
}

function ProductsTable({ products }) {
  let productsJSX = [];
  products.forEach((product) => {
    productsJSX.push(<Product product={product} key={product.name} />);
  });

  return (
    <div className="products">
      <table>
        <thead>
          <tr>
            <th style={{ textAlign: "left" }}>Name</th>

            <th style={{ textAlign: "left" }}>Price</th>
          </tr>
        </thead>
        <tbody>{productsJSX}</tbody>
      </table>
    </div>
  );
}

function Product({ product }) {
  const name = product.stocked ? (
    product.name
  ) : (
    <span style={{ color: "red" }}>{product.name}</span>
  );
  return (
    <tr>
      <td>{name}</td>
      <td>{product.price}</td>
    </tr>
  );
}

export default App;
